    int len=as3_leng-1;
    memcpy(s, as3_text+1, len);
    s[len] = 0;
    int t;
    for(t=len;t>=0;--t) {
        if(s[t]=='/') {
            s[t] = 0;
            break;
        }
    }
    a3_lval.regexp.pattern = s;
    if(t==len) {
        a3_lval.regexp.options = 0;
    } else {
        a3_lval.regexp.options = s+t+1;
    }
